import axios from "axios"
import { useEffect } from "react"

const post = [];

axios.get('/api/post')
.then((response) => {
    response.data.map((data, i) => {
        post[i] = data;
    })
})

const service = {
    getData: () => {
        return new Promise((res, rej) => {
            res({
                count : post.length,
                data : post
            })
        })
    }
}



export default service;
