import './App.css';
import React, {useEffect, useState} from 'react';
import Write from './pages/Write';
import PostPage from './pages/PostPage';
import Home from './pages/Home';
import Navbar from './components/Navbar';
import axios from 'axios';
import { Container, Snackbar } from '@mui/material';
import MuiAlert from '@mui/material/Alert';
import { Routes, Route } from 'react-router-dom';

const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const App = () => {
  console.log('App 페이지 재랜더링');

  const [user, setUser] = useState([]);
  const [posts, setPosts] = useState([]);

  const [alertOpen, setAlertOpen] = useState(false);

  useEffect(() => {
    axios.get('/api/user')
    .then((result) => {
      setUser(result.data);
    })
    .catch((error) => {
      console.log(error);
    })
  }, []);

  useEffect(() => {
    axios.get('/api/post')
    .then((result) => {
      setPosts(result.data);
    })
    .catch((error) => {
      console.log(error);
    })
  }, []);


  const handleClick = () => {
    setAlertOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setAlertOpen(false);
  };


  // const handleOnAlert = (severity, message) => {
  //   setAlert({severity : severity, message : message});
  //   document.querySelector('.write-alert').style.display = 'flex';
  //   setTimeout(() => {
  //     document.querySelector('.write-alert').style.display = 'none';
  //   }, 1000)
  // }

  return (
    <Container maxWidth={false} disableGutters>
      <Navbar user={user} />
      {/* <Alert className='write-alert' severity={alert.severity}
        sx = {{
        position : 'absolute',
        left : '0',
        right : '0',
        top : '100px',
        marginLeft : 'auto',
        marginRight : 'auto',
        width : '200px',
        display : 'none',
      }}
      >{alert.message}</Alert> */}
      <Snackbar open={alertOpen} autoHideDuration={3000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
          This is a success message!
        </Alert>
      </Snackbar>
      <Routes>
        <Route path='/' element={
          <Home posts={posts} user={user} setPosts={setPosts} />
        } />
        <Route path='/write' element={
          <Write user={user} handleClick={handleClick} /*handleOnAlert={handleOnAlert}*/ />
        } />
        <Route path='/detail/:id' element={
          <PostPage posts={posts} />
        } />
      </Routes>
    </Container>
  )
}

export default App
