import './App.css';
import React, {useEffect, useState} from 'react';
import Main from './pages/Main';
import Write from './pages/Write';
import PostPage from './pages/PostPage';
import Navbar from './components/Navbar';
import axios from 'axios';
import { Container, Alert } from '@mui/material';
import { Routes, Route } from 'react-router-dom';
import { textAlign } from '@mui/system';
import usePagination from './services/Pagination';


const pageSize = 4;

const App = () => {
  //console.log('App 페이지 재랜더링');

  const [user, setUser] = useState([]);
  const [posts, setPosts] = useState([]);
  const [alert, setAlert] = useState({});
  const [pagination, setPagination] = useState({
    count : 0,
    from : 0,
    to : pageSize
  })

  useEffect(() => {
    axios.get('/api/user')
    .then((result) => {
      setUser(result.data);
    })
    .catch((error) => {
      console.log(error);
    })
  }, []);

  useEffect(() => {
    axios.get('/api/post')
    .then((result) => {
      const extraPosts = result.data;
      setPosts(result.data);
      console.log(result);

      const usePagination = {
        getData: ({from, to}) => {
            return new Promise((resolve, reject) => {
              const data = extraPosts.slice(from, to);
                resolve({
                  count : extraPosts.length,
                  data : data
                })
            });
        }
      }

      usePagination.getData({from : pagination.from, to:pagination.to})
      .then((response) => {
        setPagination({...pagination, count : response.count});
        setPosts(response.data);
      })



    })
    .catch((error) => {
      console.log(error);
    })
  }, [])

  const handlePageChange = (event, page) => {
    const from = (page - 1) * pageSize;
    const to = (page - 1) * pageSize + pageSize;

    setPagination({...pagination, from : from, to : to});
  }



  const handleOnAlert = (severity, message) => {
    setAlert({severity : severity, message : message});
    document.querySelector('.write-alert').style.display = 'flex';
    setTimeout(() => {
      document.querySelector('.write-alert').style.display = 'none';
    }, 1000)
  }

  return (
    <Container maxWidth={false} disableGutters>
      <Navbar user={user} />

      {/* <Alert className='write-alert' severity={alert.severity}
        sx = {{
        position : 'absolute',
        left : '0',
        right : '0',
        top : '100px',
        marginLeft : 'auto',
        marginRight : 'auto',
        width : '200px',
        display : 'none',
      }}
      >{alert.message}</Alert> */}
      <Routes>
        <Route path='/' element={
          <Main posts={posts} user={user} setPosts={setPosts} pagination={pagination} pageSize={pageSize} handlePageChange={handlePageChange}/>
        } />
        {/* <Route path='/write' element={
          <Write user={user} handleOnAlert={handleOnAlert}/>
        } /> */}
        <Route path='/detail/:id' element={
          <PostPage posts={posts} />
        } />
      </Routes>
    </Container>
  )
}

export default App
