import './App.css';
import React, {useEffect, useState} from 'react';
import PostPage from './pages/PostPage';
import Home from './pages/Home';
import WritePage from './pages/WritePage';
import UpdatePage from './pages/UpdatePage';
import Navbar from './components/Navbar';
import axios from 'axios';
import { Container, Snackbar } from '@mui/material';
import MuiAlert from '@mui/material/Alert';
import { Routes, Route } from 'react-router-dom';

const Alert = React.forwardRef(function Alert(props, ref) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const App = () => {
  console.log('App 페이지 재랜더링');

  const [user, setUser] = useState([]);
  const [posts, setPosts] = useState([]);

  const [editId, setEditId] = useState(null);

  const [alertOpen, setAlertOpen] = useState(false);
  const [alertMessage, setAlertMessage] = useState({});


  useEffect(() => {
    axios.get('/api/user')
    .then((result) => {
      setUser(result.data);
    })
    .catch((error) => {
      console.log(error);
    })
  }, []);

  useEffect(() => {
    axios.get('/api/post')
    .then((result) => {
      setPosts(result.data);
    })
    .catch((error) => {
      console.log(error);
    })
  }, []);

  const parentFunction = (editId) => {
    console.log(editId);
  }


  const handleAlertClick = (severity, message) => {
    setAlertMessage({
      severity: severity,
      message : message
    })
    setAlertOpen(true);
  };

  const handleAlertClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setAlertOpen(false);
  };

  return (
    <Container maxWidth={false} disableGutters>

      <Navbar user={user} />

      <Snackbar open={alertOpen} autoHideDuration={1800} onClose={handleAlertClose}>
        <Alert onClose={handleAlertClose} severity={alertMessage.severity} sx={{ width: '100%' }}>
          {alertMessage.message}
        </Alert>
      </Snackbar>

      <Routes>
        <Route path='/' element={
          <Home posts={posts} user={user} setPosts={setPosts} />
        } />
        <Route path='/write' element={
          <WritePage handleAlertClick={handleAlertClick} />
        } />
        <Route path='/detail/:id' element={
          <PostPage posts={posts} handleAlertClick={handleAlertClick} editId={editId} setEditId={setEditId}/>
        } />
        <Route path='/edit' element={
          <UpdatePage editId={editId} />
        } />
      </Routes>

    </Container>
  )
}

export default App
