import '../scss/PostPage.scss';
import {Box, Typography} from '@mui/material';

const DetailPost = (props) => {

    return(
        <Box className='post-page-area'>
          {
            props?.post[id] ?
            <div>
              {props.post[id].title}
              {props.post[id].topic}
              <div dangerouslySetInnerHTML={{ __html : props.post[id].contents }} />

            </div>
            : <p>Loading...</p>
          }

      </Box>
    )
}

export default DetailPost;
