import React, { useState } from 'react';
import '../scss/WritePost.scss';
import { Box, Card, Typography, TextField, Button, FormControl } from '@mui/material';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axios from 'axios';


const WritePost = () => {

  const [contents, setContents] = useState("");
  const [title, setTitle] = useState("");
  const [topic, setTopic] = useState("");

  const onChangeContents = (text) => {
    setContents(text);
  }

  const onChangeTitle = (e) => {
    setTitle(e.target.value);
  }

  const onChangeTopic = (e) => {
    setTopic(e.target.value);
  }

  const onSubmit = (e) => {
    e.preventDefault();

    setContents("");
    setTitle("");
    setTopic("");

    const variables = {
      title : title,
      topic : topic,
      contents : contents
    }

    axios.post('http://localhost:8080/create', variables)
    .then((result) => {
      console.log(result);
    })
    .catch((error) => {
      console.log(error);
    })
  }

  return (
    <Box className='write-area'>
        <Card
            component='div'
            className='write-card-area'>
            <FormControl onSubmit={onSubmit} fullWidth>
              <TextField name='title' onChange={onChangeTitle} label='글 제목' variant='outlined' sx={{
                paddingBottom : '10px'
              }}/>
              <TextField name='topic' onChange={onChangeTopic} label='글 주제' variant='outlined' />
              <ReactQuill value={contents} onChange={onChangeContents} />
              <Button variant='contained' onClick={onSubmit}>버튼</Button>
            </FormControl>
        </Card>
    </Box>
  )
}

export default WritePost;
