import React from 'react';
import '../scss/Profile.scss';
import {Box, Card, Typography, Avatar, IconButton} from '@mui/material';

const Profile = (props) => {
    return(
        <Box className='profile-container'>
            <Card
                component='div'
                className='profile-card'>
                <Box className='profile-box'>
                    <Box sx={{
                        padding : '5px',
                        borderRadius : '50%',
                        border : '0.5px solid gray'
                    }}>
                    <Avatar
                        //UPDATE : 이미지 주소 넣을 것
                        sx={{
                            width : '150px',
                            height : '150px',
                        }} />
                    </Box>
                    <Typography
                        variant='h5'
                        sx={{
                            marginTop : '20px',
                            marginBottom : '10px'
                        }}
                    >
                        {props.user[0].name}
                    </Typography>
                    <Typography
                        variant='subtitle2'
                        sx={{
                            marginTop : '10px',
                            marginBottom : '10px'
                        }}
                    >
                        {props.user[0].blogExplain}
                    </Typography>
                    <Box sx={{
                        display : 'flex',
                        flexDirection : 'row',
                        marginTop : '20px',
                    }}>
                        <Box>
                            <IconButton aria-label='github' href={props.user[0].githubLink}>
                                <Avatar src='/static/images/icons/github.png' sx={{
                                    boxShadow : '0px 0px 10px gray'
                                }} />
                            </IconButton>
                        </Box>
                        <Box>
                            <IconButton href={props.user[0].instagramLink}>
                                <Avatar src='/static/images/icons/instagram.png' sx={{
                                    boxShadow : '0px 0px 10px gray',
                                }}/>
                            </IconButton>
                        </Box>
                        <Box>
                            <IconButton href={props.user[0].linkedInLink}>
                                <Avatar src='/static/images/icons/linkedin.png' sx={{
                                    boxShadow : '0px 0px 10px gray'
                                }}/>
                            </IconButton>
                        </Box>
                        <Box>
                            <IconButton href={props.user[0].homePageLink}>
                                <Avatar src='/static/images/icons/link.png' sx={{
                                    boxShadow : '0px 0px 10px gray'
                                }}/>
                            </IconButton>
                        </Box>
                    </Box>

                </Box>

            </Card>
        </Box>
    )
}

export default Profile;
