import React from 'react';
import {Container, AppBar, Toolbar, Typography, Stack, Button, Menu, MenuItem, Divider, Avatar} from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { useState } from 'react';

const Navbar = (props) => {
    const [userMenu, setUserMenu] = useState(null)

    function handleOpenUserMenu(e) {
        setUserMenu(e.currentTarget);
    }

    function handleCloseUserMenu(e) {
        setUserMenu(null);
    }

    return(
        <AppBar position='static'>
            <Container maxWidth='lg'>
                <Toolbar 
                    disableGutters
                    sx={{
                        justifyContent : 'space-between'
                    }}
                >
                    <Typography
                        variant='h5'
                        component='a'
                        href='/'
                        sx={{
                            textDecoration : 'none',
                            color : 'inherit',
                            marginRight : '70px',
                        }}
                    >
                        Blog
                    </Typography>
                    <Stack
                        direction='row'
                        divider={
                            <Divider 
                                orientation='vertical' flexItem
                            />
                        }
                        spacing={3}
                    >
                        <Button
                            sx={{
                                color : 'inherit'
                            }}
                        >홈</Button>
                        <Button
                            href='/write'
                            sx={{
                                color : 'inherit'
                            }}
                        >새 글 쓰기</Button>
                        <Button

                            sx={{
                                color : 'inherit'
                            }}
                        >마이페이지</Button>
                    </Stack>

                    <Stack 
                        direction='row' 
                    >
                        <Button
                            onClick={handleOpenUserMenu}
                            // startIcon={
                            //     <Avatar src={props.user[0].image}/>
                            // }
                            endIcon={<KeyboardArrowDownIcon />}
                            sx={{
                                color : 'inherit'
                            }}
                        >
                            <Typography>
                                {props.user[0].name}
                            </Typography>

                        </Button>
                    </Stack>
                    <Menu 
                        anchorEl={userMenu}
                        open={Boolean(userMenu)}
                        onClose={handleCloseUserMenu}
                    >
                        <MenuItem>개인정보 변경</MenuItem>
                        <MenuItem>관리자 페이지</MenuItem>
                        <Divider />
                        <MenuItem>로그아웃</MenuItem>
                    </Menu>
                </Toolbar>
            </Container>
        </AppBar>
    )
}

export default Navbar
