import React, { useState } from 'react';
import { Box, Card, Typography, TextField, Button, FormControl } from '@mui/material';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';


const WritePost = (props) => {

  const onChangeContents = (text) => {
    props.setContents(text);
  }

  return (
    <Box sx={{
        boxSizing : 'border-box',
        padding : '10px',
        width : '100%',
        height : '1100px',
    }}>
        <Card
            component='div'
            sx={{
                boxSizing : 'border-box',
                padding : '20px',
                width : '100%',
                height : '100%',
                borderRadius : '10px',
                boxShadow : '0px 0px 5px gray'
        }}>
            <FormControl fullWidth>
              <TextField name='title' fullWidth  label='글 제목' variant='outlined' />
              <ReactQuill value={props.contents} onChange={onChangeContents}/>
              <Button variant='contained' type='submit'>버튼</Button>
            </FormControl>
            <div dangerouslySetInnerHTML={{__html : props.contents}} />
        </Card>
    </Box>
  )
}

export default WritePost;
