import React from 'react';
import '../scss/Navbar.scss';
import {Container, Box, AppBar, Toolbar, Typography, Stack, Button, Menu, MenuItem, Divider, Avatar, Skeleton} from '@mui/material';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { useState } from 'react';

const Navbar = (props) => {
    const [userMenu, setUserMenu] = useState(null);

    const userNameBox = document.querySelector('.user-name-box');
    console.log(userNameBox.clientWidth);

    function handleOpenUserMenu(e) {
        setUserMenu(e.currentTarget);
    }

    function handleCloseUserMenu(e) {
        setUserMenu(null);
    }

    return(
        <AppBar position='static'>
            <Container maxWidth='lg'>
                <Toolbar 
                    disableGutters
                    className='navbar-toolbar'
                >
                    <Typography
                        variant='h5'
                        component='a'
                        href='/'
                        className='navbar-blogname'
                    >
                        Blog
                    </Typography>
                    <Stack
                        direction='row'
                        divider={
                            <Divider 
                                orientation='vertical' flexItem
                            />
                        }
                        spacing={3}
                    >
                        <Button
                            href='/'
                            className='navbar-button'
                        >홈</Button>
                        <Button
                            href='/write'
                            className='navbar-button'
                        >새 글 쓰기</Button>
                        <Button
                            href='/'
                            className='navbar-button'
                        >마이페이지</Button>
                    </Stack>

                    <Stack 
                        direction='row' 
                    >
                        <Button
                            onClick={handleOpenUserMenu}
                            startIcon={
                                <Avatar/>
                            }
                            endIcon={<KeyboardArrowDownIcon />}
                            className='navbar-button'
                        >
                            {
                                props.user.name ?
                                (
                                        <div className='user-name-box'>
                                            {props.user?.name}
                                        </div>
                                )
                                :
                                (
                                    <Typography>
                                        <Skeleton variant="rounded" width={148} height={24} />
                                    </Typography>
                                )
                            }


                        </Button>
                    </Stack>
                    <Menu 
                        anchorEl={userMenu}
                        open={Boolean(userMenu)}
                        onClose={handleCloseUserMenu}
                    >
                        <MenuItem>개인정보 변경</MenuItem>
                        <MenuItem>관리자 페이지</MenuItem>
                        <Divider />
                        <MenuItem>로그아웃</MenuItem>
                    </Menu>
                </Toolbar>
            </Container>
        </AppBar>
    )
}

export default Navbar
